mapper.update:
UPDATE DSP_EQUIPMENT
SET 
	 FACTORYID 	 	= #{factoryid}
	,EQUIPMENTID	= #{equipmentid}
	,EQUIPMENTNAME	= #{equipmentname}
	,FAILURERATE	= #{failurerate}			
	,RECOVERYTIME   = #{recoverytime}
	,ERRORRATE		= #{errorrate}
	,BUFFER   		= #{buffer}	
	,EVENTTIME 		= GETDATE()
	,ISUSABLE   	= #{isusable}
WHERE DATASEQ		= #{dataseq}
