mapper.update:
MERGE INTO DSP_SHIFT AS A
USING (
	SELECT 1 AS DUM
) AS B
ON (
	A.COMPANYID = #{companyid} AND
	A.FACTORYID = #{factoryid} AND
	A.SHIFTID = #{shiftid}
	)
WHEN MATCHED THEN
	UPDATE SET 
		 SHIFTNAME		= #{shiftname}
		,SHIFTTYPE		= #{shifttype}
		,STARTTIME		= #{starttime}
		,ENDTIME		= #{endtime}
		,ISUSABLE		= #{isusable}
WHEN NOT MATCHED THEN 
	INSERT(
		 COMPANYID
		,FACTORYID
		,SHIFTID
		,SHIFTNAME
		,SHIFTTYPE
		,STARTTIME
		,ENDTIME
		,ISUSABLE				
	) 
	VALUES(
		 'dx'
		,#{factoryid}
		,#{shiftid}
		,#{shiftname}				
		,#{shifttype}
		,#{starttime}
		,#{endtime}
		,#{isusable}				
	)
;

update.foreach:
		UPDATE DSP_SHIFT 
		SET ISUSABLE= 'N'
    	WHERE
			COMPANYID = 'dx' 
			AND FACTORYID = #{data.factoryid} 
			AND SHIFTID = #{data.shiftid}
