mapper.select:
SELECT COUNT(*)
FROM DSP_SHIFT
WHERE 1=1
	AND SHIFTID = #{shiftid}
	AND FACTORYID = #{factoryid}

mapper.update:
MERGE INTO DSP_SHIFT AS A
USING (
	SELECT 1 AS DUM
) AS B
ON (
	A.FACTORYID = #{factoryid} AND
	A.SHIFTID = #{shiftid}
	)
WHEN MATCHED THEN
	UPDATE SET 
		 SHIFTNAME		= #{shiftname}
		,SHIFTTYPE		= #{shifttype}
		,STARTTIME		= #{starttime}
		,ENDTIME		= #{endtime}
		,EVENTUSER		= 'idrAdmin'
		,EVENTTIME		= GETDATE()
		,ISUSABLE		= #{isusable}
WHEN NOT MATCHED THEN 
	INSERT(
		 COMPANYID
		,FACTORYID
		,SHIFTID
		,SHIFTNAME
		,SHIFTTYPE
		,STARTTIME
		,ENDTIME
		,CREATOR
		,CREATETIME
		,EVENTUSER
		,EVENTTIME				
		,ISUSABLE				
	) 
	VALUES(
		 'dx'
		,#{factoryid}
		,#{shiftid}
		,#{shiftname}				
		,#{shifttype}
		,#{starttime}
		,#{endtime}
		,'idrAdmin'
		,GETDATE()
		,'idrAdmin'
		,GETDATE()				
		,#{isusable}				
	)
;
