mapper.insert:
INSERT INTO DSP_INVENTORY (
       FACTORYID
       , STORAGEID
       , MATERIALID
       , QTY
       , CREATOR
       , CREATETIME
       , EVENTUSER
       , EVENTTIME
       , INVENTORYDATE
)
VALUES (
       #{factoryid}
       , #{storageid}
       , #{materialid}
       , #{qty}
       , #{creator}
       , getdate()
       , #{eventuser}
       , getdate()
       , #{inventorydate}
)

mapper.update:
UPDATE DSP_INVENTORY
   SET
   	   FACTORYID		=	#{factoryid}
       , STORAGEID		=	#{storageid}
       , MATERIALID		=	#{materialid}
       , QTY			=	#{qty}
       , EVENTUSER		=	#{eventuser}
       , EVENTTIME		=	getdate()
       , INVENTORYDATE	=	#{inventorydate}
 WHERE DATASEQ          = 	#{dataseq}
