mapper.select:
        SELECT 
        	COUNT(*) AS val
		FROM DSP_LOCATION A
		WHERE FACTORYID  = #{factoryid}
		AND LOCATIONID  = #{locationid}

mapper.update:
UPDATE DSP_LOCATION
SET 
	 FACTORYID		= #{factoryid}
	,LOCATIONID     = #{locationid}
	,LOCATIONNAME   = #{locationname}
	,LOCATIONTYPE	='LINE'
	,EVENTTIME 		= GETDATE()
	,ISUSABLE		= #{isusable}
WHERE DATASEQ		= #{dataseq}
