mapper.update:
UPDATE DSP_LOTINFO
SET 
	 FACTORYID 	 	= #{factoryid}
	,MATERIALID		= #{materialid}
	,MATERIALNAME	= #{materialname}
	,LOTID			= #{lotid}			
	,LOTNAME   		= #{lotname}
	,LOTSIZE		= #{lotsize}
	,LOTUNIT    	= #{lotunit}	
	,FROMLINECODE   = #{fromlinecode}
	,FROMLINECNT   	= #{fromlinecnt}
	,TOLINECODE   	= #{tolinecode}
	,TOLINECNT   	= #{tolinecnt}
	,EVENTTIME 		= GETDATE()
	,ISUSABLE   	= #{isusable}
WHERE DATASEQ		= #{dataseq}
