mapper.update:
UPDATE DSP_MATERIAL
SET 
	 MATERIALID		= #{materialid}
	,FACTORYID 	 	= #{factoryid}
	,MATERIALNAME	= #{materialname}
	,MATERIALTYPE	= #{materialtype}			
	,MATERIALKIND   = #{materialkind}
	,MATERIALUNIT	= #{materialunit}
	,VENDORID    	= #{vendorid}	
	,RECEIVINGINSPECTION   = #{receivinginspection}
	,EVENTUSER 		='idrAdmin'
	,EVENTTIME 		= GETDATE()
	,ISUSABLE   	= #{isusable}
WHERE DATASEQ		= #{dataseq}
