mapper.select:
        SELECT 
        		COUNT(*) AS val
		 FROM DSP_WORKDAILY_REPORT A
		WHERE ISUSABLE IS NOT NULL
		AND ISUSABLE = 'Usable'
		AND LINEID =#{lineid} 
		AND SHIFTID =#{shiftid}
		AND WORK_DATE =CONVERT(VARCHAR, CONVERT(DATE, #{workDate}), 112)
		AND FACTORYID  =#{factoryid}

mapper.insert:
INSERT INTO DSP_WORKDAILY_REPORT (
       RULESYSID
     , FACTORYID
     , WORK_DATE
     , LINEID
     , BLOCKID
     , GROUPID
     , SHIFTID
     , REGISTER
     , REVIEWER
     , APPROVER
     , NOTES
     , CREATOR
     , CREATETIME
     , EVENT
     , EVENTUSER
     , EVENTTIME
     , ISUSABLE
     , TID
)
VALUES (
       1
     , #{factoryid}
     , CONVERT(VARCHAR, CONVERT(DATE, #{workDate}), 112)
     , #{lineid}
     , #{blockid}
     , #{groupid}
     , #{shiftid}
     , #{register}
     , #{reviewer}
     , #{approver}
     , #{notes}
     , #{creator}
     , GETDATE()
     , #{event}
     , #{eventuser}
     , #{eventtime}
     ,'Usable'
     , #{tid}
)

mapper.update:
UPDATE DSP_WORKDAILY_REPORT
   SET
   	   WORK_DATE		= REPlACE(#{workDate}, '-', '')
   	 , FACTORYID		= #{factoryid}
     , BLOCKID          = #{blockid}
     , LINEID			= #{lineid}
     , GROUPID          = #{groupid}
     , SHIFTID          = #{shiftid}
     , NOTES			= #{notes}
     , EVENTTIME = GETDATE()
     ,REVIEWER=#{reviewer}
     ,APPROVER=#{approver}
 WHERE DATASEQ          = #{dataseq}
