UPDATE DSP_WORK_CONTENTS
SET
WORKTIME_FROM = #{worktimeFrom}
, WORKTIME_TO = #{worktimeTo}
, MANHOUR = #{manhour}
, PROD_QTY = #{prodQty}
, GOODSUM_QTY = #{goodsumQty}
, REWORK_GOOD_QTY = #{reworkGoodQty}
, REWORK_FAIL_QTY = #{reworkFailQty}
, NOTES = #{notes}
, EVENTTIME = GETDATE()
WHERE
DATASEQ = #{dataseq}
